    event CreditLimitSet(address indexed user, uint256 limit, uint256 creditScore);
    event CreditSpent(
        uint256 indexed transactionId,
        address indexed user, 
        address indexed merchant,
        uint256 amount, 
        string purpose,
        uint256 dueDate,
        uint256 spndRewards,
        uint256 btcCashback
    );
    event CreditRepaid(
        uint256 indexed transactionId,
        address indexed user, 
        uint256 amount, 
        uint256 newBalance
    );
